-- =========================================
--  DISPO_VEHICULE - Distribuir 100 veículos
-- =========================================
WITH RandomNumbers AS (
    SELECT 
        ID_VEHIC,
        CAST(CHECKSUM(NEWID()) AS FLOAT) / CHECKSUM(NEWID()) AS RandomNumber
    FROM FLOTTE
)

SELECT 
    NTILE(5) OVER (ORDER BY RandomNumber) AS ID_AGE,
    F.ID_VEHIC,
    'DISPONIBLE'
FROM FLOTTE F
JOIN RandomNumbers RN ON F.ID_VEHIC = RN.ID_VEHIC
ORDER BY RandomNumber;


-- =========================================
--  CLIENTS (50 enregistrements)
-- =========================================
DECLARE @index INT = 1;
WHILE @index <= 50
BEGIN
        NOM, PRENOM, ADRESSE, PERMIS_COND, HIST_ACCIDENTS, EMAIL, TELEPHONE, CARTE_CRED
    )
    VALUES (
        CONCAT('CLEINTNOM', @index), 
        CONCAT('PRENOM', @index),
        CONCAT(@index, ' RUE FICTIVE, VILLE MONTREAL'),
        CONCAT('ABC', RIGHT('0000'+CAST(@index AS NVARCHAR(4)),4)),  
        0, 
        CONCAT('client', @index, '@mail.com'),
        CONCAT('+1 ', CAST(2 + (@index % 8) AS VARCHAR(1)), 
                    CAST(10 + (@index % 90) AS VARCHAR(2)), '-', 
                    CAST(2 + (@index % 8) AS VARCHAR(1)), 
                    CAST(10 + (@index % 90) AS VARCHAR(2)), '-', 
                    CAST(1000 + (@index % 9000) AS VARCHAR(4))),
        CONCAT(REPLICATE('0', 4 - LEN(@index)), @index)
    );

    SET @index += 1;
END;


